import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CustomWeather() {
  const [customWeatherData, setCustomWeatherData] = useState([]);
  const [city, setCity] = useState(''); // Initialize city state with an empty string
  const [temperature, setTemperature] = useState('');
  const [weatherCondition, setWeatherCondition] = useState('');

  // Function to fetch custom weather data from the server based on the entered city
  const fetchCustomWeatherData = async (searchCity) => { // Accept the searchCity as a parameter
    try {
      // Send a GET request to fetch custom weather data for the specified city
      const response = await axios.get('http://localhost:5000/api/customWeather', {
        params: { city: searchCity }, // Use the searchCity as the query parameter
      });

      setCustomWeatherData(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(() => {
    // Fetch custom weather data when the component mounts
    fetchCustomWeatherData(city); // Pass the current value of 'city' to fetchCustomWeatherData
  }, [city]); // Trigger the effect whenever 'city' changes

  const handleSubmit = async (e) => {
    e.preventDefault();
    // When the user submits the form, fetch custom weather data for the entered city
    fetchCustomWeatherData(city);
  };

  return (
    <div className="custom-weather-form">
      <h2>Add Custom Weather Data</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="city">City:</label>
          <input
            type="text"
            id="city"
            value={city}
            onChange={(e) => setCity(e.target.value)} // Update 'city' state as the user types
            required
          />
        </div>
        {/* ... (other form fields) */}
        <button type="submit">Search Custom Weather Data</button>
      </form>

      {/* Display custom weather data */}
      <h2>Custom Weather Data</h2>
      <ul>
        {customWeatherData.map((item) => (
          <li key={item._id}>
            City: {item.city}, Temperature: {item.temperature}, Condition: {item.weatherCondition}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CustomWeather;
