// AccountSettings.js
import React, { useState } from 'react';
import axios from 'axios'; // Import Axios or your preferred HTTP client library

function AccountSettings({ user }) {
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [error, setError] = useState('');

  const handleUpdateUsername = async () => {
    try {
      // Make an API request to update the username
      await axios.put('/api/update-username', { userId: user._id, newUsername });
      // Update the user object with the new username
      user.username = newUsername;
      setNewUsername('');
      setError('');
    } catch (error) {
      console.error('Error updating username:', error);
      setError('Error updating username. Please try again.');
    }
  };

  const handleUpdatePassword = async () => {
    try {
      // Make an API request to update the password
      await axios.put('/api/update-password', { userId: user._id, newPassword });
      setNewPassword('');
      setError('');
    } catch (error) {
      console.error('Error updating password:', error);
      setError('Error updating password. Please try again.');
    }
  };

  const handleDeleteAccount = async () => {
    try {
      // Make an API request to delete the user's account
      await axios.delete('/api/delete-account', { data: { userId: user._id } });
      // Redirect to the homepage or perform any necessary actions
    } catch (error) {
      console.error('Error deleting account:', error);
      setError('Error deleting account. Please try again.');
    }
  };

  return (
    <div className="account-settings">
      <h2>Account Settings</h2>
      <div className="username-section">
        <h3>Change Username</h3>
        <input
          type="text"
          placeholder="New Username"
          value={newUsername}
          onChange={(e) => setNewUsername(e.target.value)}
        />
        <button onClick={handleUpdateUsername}>Update Username</button>
      </div>
      <div className="password-section">
        <h3>Change Password</h3>
        <input
          type="password"
          placeholder="New Password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button onClick={handleUpdatePassword}>Update Password</button>
      </div>
      <div className="delete-account-section">
        <h3>Delete Account</h3>
        <button onClick={handleDeleteAccount}>Delete My Account</button>
      </div>
      {error && <p className="error">{error}</p>}
    </div>
  );
}
