// AccountSettings.js
import React, { useState } from 'react';

function AccountSettings() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Add functions to handle username and password updates
  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  // Add a function to handle account deletion
  const handleDeleteAccount = () => {
    // Implement the logic to delete the user's account here
    // You can send a DELETE request to your server to delete the account
    // Don't forget to handle authentication to ensure the user is logged in before deleting the account
  };

  return (
    <div className="account-settings">
      <h2>Account Settings</h2>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      <button onClick={handleDeleteAccount}>Delete Account</button>
    </div>
  );
}

export default AccountSettings;
