// App.js
import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Weather from './components/Weather';
import Login from './components/Login';
import Register from './components/Register';
import CustomWeather from './components/CustomWeather';
import AccountSettings from './components/AccountSettings'; // Import the AccountSettings component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
    console.log('User is now logged in.');
  };

  const handleRegister = () => {
    setIsLoggedIn(true);
    console.log('User is now registered');
  };

  return (
    <Router>
      <div className={`App${isLoggedIn ? ' logged-in' : ''}`}>
        {isLoggedIn && (
          <nav className="navbar">
            <ul className="nav-menu">
              <li className="nav-item">
                <Link to="/weather" className="nav-link">
                  Weather
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/custom-weather" className="nav-link">
                  Custom Weather
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/account-settings" className="nav-link">
                  Account Settings
                </Link>
              </li>
            </ul>
          </nav>
        )}

        <Routes>
          <Route
            path="/"
            element={
              <div className="home-page">
                <header>
                  <h1 className="app-title">Welcome to Conor's Weather App</h1>
                </header>
                {!isLoggedIn ? (
                  <section className="login-register-section">
                    <div className="buttons-container">
                      <Link to="/login" className="button-style">
                        Login
                      </Link>
                      <Link to="/register" className="button-style">
                        Register
                      </Link>
                    </div>
                  </section>
                ) : (
                  <section className="logged-in-section">
                    <Navigate replace to="/weather" />
                  </section>
                )}
              </div>
            }
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register onRegister={handleRegister} />} />
          {isLoggedIn && (
            <>
              <Route path="/weather" element={<Weather />} />
              <Route path="/custom-weather" element={<CustomWeather />} />
              <Route path="/account-settings" element={<AccountSettings />} /> {/* Add the AccountSettings route */}
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
