import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CustomWeather() {
  const [customWeatherData, setCustomWeatherData] = useState([]);
  const [city, setCity] = useState('');
  const [temperature, setTemperature] = useState('');
  const [weatherCondition, setWeatherCondition] = useState('');

  // Function to fetch custom weather data from the server
  const fetchCustomWeatherData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/customWeather');
      setCustomWeatherData(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(() => {
    // Fetch custom weather data when the component mounts
    fetchCustomWeatherData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send a POST request to add custom weather data
      const response = await axios.post('http://localhost:5000/api/customWeather', {
        city,
        temperature,
        weatherCondition,
      });

      if (response.status === 201) {
        // Custom weather data added successfully
        // Refresh the custom weather data after adding
        fetchCustomWeatherData();
        // Reset input fields
        setCity('');
        setTemperature('');
        setWeatherCondition('');
      } else {
        // Handle errors if necessary
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="custom-weather-form">
      <h2>Add Custom Weather Data</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="city">City:</label>
          <input
            type="text"
            id="city"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="temperature">Temperature:</label>
          <input
            type="number"
            id="temperature"
            value={temperature}
            onChange={(e) => setTemperature(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="weatherCondition">Weather Condition:</label>
          <input
            type="text"
            id="weatherCondition"
            value={weatherCondition}
            onChange={(e) => setWeatherCondition(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Custom Weather Data</button>
      </form>

      {/* Display custom weather data */}
      <h2>Custom Weather Data</h2>
      <ul>
        {customWeatherData.map((item) => (
          <li key={item._id}>
            City: {item.city}, Temperature: {item.temperature}, Condition: {item.weatherCondition}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CustomWeather;
