import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';

import Weather from './components/Weather';
import CustomWeather from './components/CustomWeather';
import Login from './components/Login';
import Register from './components/Register';
import AccountSettings from './components/AccountSettings';
import NavigationBar from './components/NavigationBar';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [isRegistered, setIsRegistered] = useState(false);

  useEffect(() => {
    const checkLoggedIn = async () => {
      try {
        const response = await axios.get('/api/check-auth');
        if (response.data.isLoggedIn) {
          setIsLoggedIn(true);
          setUser(response.data.user);
        } else {
          <Navigate to="/login" />;
        }
      } catch (error) {
        console.error('Error checking authentication:', error);
      }
    };

    checkLoggedIn();
  }, []);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleRegister = () => {
    setIsLoggedIn(true);
    setIsRegistered(true);
  };

  const handleLogout = async () => {
    try {
      await axios.post('/api/logout');
      setIsLoggedIn(false);
      setUser(null);
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <Router>
      <NavigationBar isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<div>Welcome to the Weather App</div>} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register onRegister={handleRegister} />} />
        {isLoggedIn && (
          <>
            <Route path="/weather" element={<Weather />} />
            <Route path="/custom-weather" element={<CustomWeather />} />
            <Route path="/account-settings" element={<AccountSettings user={user} />} />
          </>
        )}
        {/* Redirect to login if not authenticated */}
        {!isLoggedIn && <Route path="*" element={<Navigate replace to="/login" />} />}
      </Routes>
    </Router>
  );
}

export default App;
