import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Weather() {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [customCity, setCustomCity] = useState('');
    const [customTemperature, setCustomTemperature] = useState('');
    const [customCondition, setCustomCondition] = useState('');
    const [customWeatherData, setCustomWeatherData] = useState([]);
    const [error, setError] = useState('');

    // Function to fetch real-time weather data
    const fetchWeatherData = async () => {
        setError('');
        try {
            const response = await axios.get(`/api/weather?city=${city}`);
            setWeatherData(response.data);
        } catch (err) {
            console.error('Server Error:', err);
            setError('Error fetching real-time weather data.');
        }
    };

    // Function to handle custom weather data submission
    const handleCustomWeatherSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/customWeather', {
                city: customCity,
                temperature: customTemperature,
                weatherCondition: customCondition,
            });
            setCustomCity('');
            setCustomTemperature('');
            setCustomCondition('');
            fetchCustomWeatherData(); // Refresh the custom weather data list
        } catch (error) {
            console.error('Error adding custom weather data:', error);
            setError('Error adding custom weather data.');
        }
    };

    // Function to fetch custom weather data
    const fetchCustomWeatherData = async () => {
        try {
            const response = await axios.get('/api/customWeather');
            setCustomWeatherData(response.data);
        } catch (error) {
            console.error('Error fetching custom weather data:', error);
            setError('Error fetching custom weather data.');
        }
    };

    // Fetch custom weather data on component mount
    useEffect(() => {
        fetchCustomWeatherData();
    }, []);

    return (
        <div className="weather-container">
            <h2>Weather Forecast</h2>
            <div>
                <input
                    type="text"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter city name"
                />
                <button onClick={fetchWeatherData}>Search</button>
            </div>
            {error && <p className="error">{error}</p>}
            {weatherData && (
                <div className="weather-data">
                    <h3>{weatherData.city}</h3>
                    <p>Temperature: {weatherData.temperature}Â°C</p>
                    <p>Condition: {weatherData.description}</p>
                </div>
            )}

            <h2>Add Custom Weather Data</h2>
            <form onSubmit={handleCustomWeatherSubmit}>
                <input
                    type="text"
                    value={customCity}
                    onChange={(e) => setCustomCity(e.target.value)}
                    placeholder="City"
                    required
                />
                <input
                    type="number"
                    value={customTemperature}
                    onChange={(e) => setCustomTemperature(e.target.value)}
                    placeholder="Temperature"
                    required
                />
                <input
                    type="text"
                    value={customCondition}
                    onChange={(e) => setCustomCondition(e.target.value)}
                    placeholder="Weather Condition"
                    required
                />
                <button type="submit">Add Custom Weather</button>
            </form>

            <h2>Custom Weather Data</h2>
            <ul>
                {customWeatherData.map((item) => (
                    <li key={item._id}>
                        City: {item.city}, Temperature: {item.temperature}, Condition: {item.weatherCondition}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Weather;
