import React, { useState } from 'react';
import axios from 'axios';

function Weather() {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState('');
    const [source, setSource] = useState('realtime');

    const fetchWeatherData = async () => {
        setError('');
        setWeatherData(null);
        try {
            let response;
            if (source === 'realtime') {
                response = await axios.get(`/api/weather?city=${city}`);
            }

            setWeatherData(response.data);
            setError(''); // Clear any previous errors
        } catch (err) {
            console.error('Server Error:', err);
            if (err.response) {
                console.error('Status:', err.response.status);
                console.error('Data:', err.response.data);
                setError('Server error occurred.');
            } else if (err.request) {
                console.error('Request Error:');
                console.error('Request:', err.request);
                setError('Request error occurred.');
            } else {
                console.error('General Error:', err.message);
                setError('Error occurred.');
            }
        }
    };

    return (
        <div className="weather-container">
            <h2>Weather Forecast</h2>
            <div>
                <label>
                    <input
                        type="radio"
                        value="realtime"
                        checked={source === 'realtime'}
                        onChange={() => setSource('realtime')}
                    />
                    Real-time Weather
                </label>
            </div>
            <div className="input-container">
                <input
                    type="text"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter city name"
                />
                <button onClick={fetchWeatherData}>Search</button>
            </div>
            {error && <p className="error">{error}</p>}
            {weatherData && (
                <div className="weather-data">
                    <h3>{weatherData.city}</h3>
                    <p>Temperature: {weatherData.temperature}Â°C</p>
                    <p>Condition: {weatherData.description}</p>
                </div>
            )}
        </div>
    );
}

export default Weather;
