import React, { useState } from 'react';
import axios from 'axios';

function CustomWeather() {
  const [city, setCity] = useState('');
  const [temperature, setTemperature] = useState('');
  const [weatherCondition, setWeatherCondition] = useState('');
  const [message, setMessage] = useState(''); // To display success or error messages

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const response = await axios.post('http://localhost:5000/api/customWeather', {
        city, temperature, weatherCondition
      });
      console.log('Response:', response.data);
      setMessage('Custom weather data added successfully.');
    } catch (error) {
      console.error('Error:', error);
      setMessage('Failed to add custom weather data.');
    }
  };

  return (
    <div className="custom-weather-form">
      <h2>Add Custom Weather Data</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="city">City:</label>
          <input
            type="text"
            id="city"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="temperature">Temperature (Â°C):</label>
          <input
            type="number"
            id="temperature"
            value={temperature}
            onChange={(e) => setTemperature(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="weatherCondition">Weather Condition:</label>
          <input
            type="text"
            id="weatherCondition"
            value={weatherCondition}
            onChange={(e) => setWeatherCondition(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Custom Weather Data</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CustomWeather;
