// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Weather from './components/Weather';
import Login from './components/Login';
import Register from './components/Register';
import CustomWeather from './components/CustomWeather';
import AccountSettings from './components/AccountSettings'; // Import AccountSettings
import NavigationBar from './components/NavigationBar'; // Import NavigationBar
import axios from 'axios'; // Import Axios or your preferred HTTP client library

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null); // User data

  useEffect(() => {
    // Check if the user is already logged in when the component mounts
    const checkLoggedIn = async () => {
      try {
        // Make an API request to check if the user is authenticated
        const response = await axios.get('/api/check-auth');

        if (response.data.isLoggedIn) {
          setIsLoggedIn(true);
          setUser(response.data.user); // Set user data
        }
      } catch (error) {
        console.error('Error checking authentication:', error);
      }
    };

    checkLoggedIn();
  }, []);

  const handleLogin = () => {
    setIsLoggedIn(true);
    console.log('User is now logged in.');
  };

  const handleRegister = () => {
    setIsLoggedIn(true);
    console.log('User is now registered');
  };

  const handleLogout = async () => {
    try {
      // Make an API request to log out the user
      await axios.post('/api/logout');
      setIsLoggedIn(false);
      setUser(null);
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <Router>
      <div className={`App${isLoggedIn ? ' logged-in' : ''}`}>
        <NavigationBar isLoggedIn={isLoggedIn} handleLogout={handleLogout} />

        <Routes>
          <Route
            path="/"
            element={
              <div className="home-page">
                <header>
                  <h1 className="app-title">Welcome to Conor's Weather App</h1>
                </header>
                {!isLoggedIn ? (
                  <section className="login-register-section">
                    <div className="buttons-container">
                      <Link to="/login" className="button-style">
                        Login
                      </Link>
                      <Link to="/register" className="button-style">
                        Register
                      </Link>
                    </div>
                  </section>
                ) : (
                  <section className="logged-in-section">
                    <Navigate replace to="/weather" />
                  </section>
                )}
              </div>
            }
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register onRegister={handleRegister} />} />
          {isLoggedIn && (
            <>
              <Route path="/weather" element={<Weather />} />
              <Route path="/custom-weather" element={<CustomWeather />} />
              <Route path="/account-settings" element={<AccountSettings user={user} />} />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
