import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Register({ onRegister }) {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isRegistered, setIsRegistered] = useState(false); // Add a state to track registration status

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }),
      });

      if (response.status === 201) {
        // Registration successful
        setIsRegistered(true); // Set registration status to true

        // Clear form fields
        setUsername('');
        setEmail('');
        setPassword('');
      } else {
        const data = await response.json();
        setError(data.error);
      }
    } catch (err) {
      console.error('Error:', err);
      setError('Server error');
    }
  };

  return (
    <div className="weather-container">
      <h2>Register</h2>
      {isRegistered ? (
        // Display success message and login button
        <>
          <p className="success">User registered successfully!</p>
          <Link to="/login" className="button-style">
            Login
          </Link>
        </>
      ) : (
        // Display registration form
        <>
          {error && <p className="error">{error}</p>}
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="username">Username:</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div>
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div>
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="button-style">
              Register
            </button>
          </form>
          <p>Have an account already? <Link to="/login">Login here</Link></p>
        </>
      )}
    </div>
  );
}

export default Register;
