import React, { useState } from 'react';

function CustomWeather() {
    const [city, setCity] = useState('');
    const [temperature, setTemperature] = useState('');
    const [weatherCondition, setWeatherCondition] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:5000/api/customWeather', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ city, temperature, weatherCondition }),
            });

            if (response.status === 201) {
                // Custom weather data added successfully
               
            } else {
              
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className="custom-weather-form">
            <h2>Add Custom Weather Data</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="city">City:</label>
                    <input
                        type="text"
                        id="city"
                        value={city}
                        onChange={(e) => setCity(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="temperature">Temperature:</label>
                    <input
                        type="number"
                        id="temperature"
                        value={temperature}
                        onChange={(e) => setTemperature(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="weatherCondition">Weather Condition:</label>
                    <input
                        type="text"
                        id="weatherCondition"
                        value={weatherCondition}
                        onChange={(e) => setWeatherCondition(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Add Custom Weather Data</button>
            </form>
        </div>
    );
}

export default CustomWeather;
