import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CustomWeather.css'; // Import your CSS file

function CustomWeather() {
  const [customWeatherData, setCustomWeatherData] = useState([]);
  const [city, setCity] = useState('');
  const [temperature, setTemperature] = useState('');
  const [weatherCondition, setWeatherCondition] = useState('');
  const [error, setError] = useState('');

  const fetchCustomWeatherData = async () => {
    try {
      const response = await axios.get('/customWeather'); // Updated URL
      setCustomWeatherData(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching custom weather data:', error);
      setError('Error fetching custom weather data.');
    }
  };

  useEffect(() => {
    fetchCustomWeatherData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/customWeather', {
        city,
        temperature,
        weatherCondition,
      });

      if (response.status === 201) {
        fetchCustomWeatherData();
        setCity('');
        setTemperature('');
        setWeatherCondition('');
        setError('');
      } else {
        setError('Error adding custom weather data.');
        console.error('Error adding custom weather data:', response.data.error);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Error adding custom weather data.');
    }
  };

  return (
    <div className="custom-weather-form">
      <h2>Add Custom Weather Data</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <input
            type="text"
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
        </div>
        <div className="input-container">
          <input
            type="number"
            placeholder="Temperature"
            value={temperature}
            onChange={(e) => setTemperature(e.target.value)}
            required
          />
        </div>
        <div className="input-container">
          <input
            type="text"
            placeholder="Weather Condition"
            value={weatherCondition}
            onChange={(e) => setWeatherCondition(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="button-style">Add Custom Weather Data</button>
      </form>

      <h2>Custom Weather Data</h2>
      {error && <p className="error">{error}</p>}
      <ul className="weather-data">
        {customWeatherData.map((item) => (
          <li key={item._id}>
            City: {item.city}, Temperature: {item.temperature}, Condition: {item.weatherCondition}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CustomWeather;
