import React, { useState } from 'react';
import axios from 'axios';

function Weather() {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState('');
    const [source, setSource] = useState('realtime'); // 'realtime' or 'custom'

    const fetchWeatherData = async () => {
        setError('');
        setWeatherData(null);
        try {
            let response;
            if (source === 'realtime') {
                response = await axios.get(`http://localhost:5000/api/weather`, {
                    params: { city }
                });
            } else {
                response = await axios.get(`http://localhost:5000/api/customWeather`, {
                    params: { city }
                });
            }
            setWeatherData(response.data);
        } catch (err) {
            console.error('Error:', err);
            setError('Error fetching weather data.');
        }
    };

    return (
        <div className="weather-container">
            <h2>Weather Forecast</h2>
            <div>
                <label>
                    <input
                        type="radio"
                        value="realtime"
                        checked={source === 'realtime'}
                        onChange={() => setSource('realtime')}
                    />
                    Real-time Weather
                </label>
                <label>
                    <input
                        type="radio"
                        value="custom"
                        checked={source === 'custom'}
                        onChange={() => setSource('custom')}
                    />
                    Custom Weather
                </label>
            </div>
            <div className="input-container">
                <input
                    type="text"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter city name"
                />
                <button onClick={fetchWeatherData}>Search</button>
            </div>
            {error && <p className="error">{error}</p>}
            {weatherData && (
                <div className="weather-data">
                    <h3>{weatherData.city}</h3>
                    <p>Temperature: {weatherData.temperature}Â°C</p>
                    <p>Condition: {weatherData.description || weatherData.weatherCondition}</p>
                </div>
            )}
        </div>
    );
}

export default Weather;
