// App.js
import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Weather from './components/Weather';
import Login from './components/Login';
import Register from './components/Register';
import CustomWeather from './components/CustomWeather';
import NavigationBar from './components/NavigationBar'; // Import the NavigationBar component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
    console.log('User is now logged in.');
  };

  const handleRegister = () => {
    setIsLoggedIn(true);
    console.log('User is now registered');
  };

  return (
    <Router>
      <div className="App">
        <NavigationBar /> {/* Include the NavigationBar component */}
        <Routes>
          <Route
            path="/"
            element={
              <div className="home-page">
                <header>
                  <h1>Welcome to Conor's Weather App</h1>
                </header>
                {!isLoggedIn ? (
                  <section className="login-register-section">
                    <div className="buttons-container">
                      <Link to="/login" className="button-style">
                        Login
                      </Link>
                      <Link to="/register" className="button-style">
                        Register
                      </Link>
                    </div>
                  </section>
                ) : (
                  <section className="logged-in-section">
                    <Navigate replace to="/weather" />
                  </section>
                )}
              </div>
            }
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register onRegister={handleRegister} />} />
          <Route path="/weather" element={isLoggedIn ? <Weather /> : <Navigate replace to="/login" />} />
          <Route path="/custom-weather" element={isLoggedIn ? <CustomWeather /> : <Navigate replace to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
